import React, { Component } from 'react';
import Modal from 'react-awesome-modal';
import './Modal.css'
export default class Examples extends Component {
    constructor(props) {
        super(props);
        this.state = {
            visible: false
        }
    }

    openModal() {
        this.setState({
            visible: true
        });
    }

    closeModal() {
        this.setState({
            visible: false
        });
    }

    render() {
        return (
            <section
                style={{
                    position: 'absolute',
                    zIndex: 800,
                    bottom: 10,
                    marginLeft: 'auto',
                    marginRight: 'auto',
                    left: 0,
                    right: 0,
                    width: 'fit-content',
                }}
            >
                <input type="button" value="UP" onClick={() => this.openModal()} />
                <Modal
                    visible={this.state.visible}
                    width="90%"
                    height="70%"
                    effect="fadeInUp"
                    onClickAway={() => this.closeModal()}
                >
                    <div>
                        <h1>Title</h1>
                        <p>Some Contents</p>
                        <a href="javascript:void(0);" onClick={() => this.closeModal()}>Close</a>
                    </div>
                </Modal>
            </section>
        );
    }
}
